mapper.update:
UPDATE	BOARD
SET		BOARD_DEL_FL = 'Y'
WHERE	MEMBER_NO    = #{memberNo}
  AND	BOARD_NO     = #{boardNo}

UPDATE	"BOARD"
SET		BOARD_TITLE   = #{boardTitle},
        BOARD_CONTENT = #{boardContent}
WHERE	BOARD_CODE    = 3
  AND	BOARD_NO      = #{boardNo}
  AND	MEMBER_NO     = #{memberNo}

UPDATE	"BOARD_IMG"
SET		IMG_RENAME        = #{imgRename}
WHERE	BOARD_NO          = #{boardNo}
  AND	IMG_ORDER         = #{imgOrder}
